import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import BookDemoModal from './components/BookDemoModal';
import { motion, AnimatePresence } from 'framer-motion';

// Animation variants
const containerVariants = {
  hidden: { opacity: 0 },
  visible: {
    opacity: 1,
    transition: {
      staggerChildren: 0.1,
      delayChildren: 0.3
    }
  }
};

const itemVariants = {
  hidden: { y: -20, opacity: 0 },
  visible: {
    y: 0,
    opacity: 1,
    transition: {
      type: 'spring',
      stiffness: 100,
      damping: 15
    }
  }
};

const dropdownVariants = {
  hidden: { opacity: 0, y: -10 },
  visible: {
    opacity: 1,
    y: 0,
    transition: {
      type: 'spring',
      damping: 20,
      stiffness: 300
    }
  },
  exit: { opacity: 0, y: -10 }
};

function Navbar() {
  const [activeItem, setActiveItem] = useState(null);
  const [hoveredItem, setHoveredItem] = useState(null);
  const [showDemoModal, setShowDemoModal] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const cursorRef = useRef(null);
  const navbarRef = useRef(null);
  const navigate = useNavigate();
  const location = useLocation();

  // Navigation items
  const navItems = [
    { 
      name: 'Communities', 
      path: '/',
      dropdown: [
        { name: 'Apartments', path: '/apartments' },
        { name: 'Gated Communities', path: '/gated-communities' },
        { name: 'Villas', path: '/villas' },
        { name: 'Plotted Development', path: '/plotted' }
      ]
    },
    { 
      name: 'Locks', 
      path: '/locks',
      dropdown: [
        { name: 'Smart Locks', path: '/smart-locks' },
        { name: 'Digital Locks', path: '/digital-locks' },
        { name: 'Access Control', path: '/access-control' }
      ]
    },
    { 
      name: 'Brands', 
      path: '/brands',
      dropdown: [
        { name: 'Our Technology', path: '/technology' },
        { name: 'Partners', path: '/partners' },
        { name: 'Success Stories', path: '/success-stories' }
      ]
    },
    { 
      name: 'Business', 
      path: '/business'
      // No dropdown for Business
    },
    { 
      name: 'Company', 
      path: '/company',
      dropdown: [
        { name: 'About Us', path: '/about' },
        { name: 'Careers', path: '/careers' },
        { name: 'Blog', path: '/blog' },
        { name: 'Contact', path: '/contact' }
      ]
    },
    { 
      name: 'Login', 
      path: '/login'
      // No dropdown for Login
    }
  ];

  const handleNavClick = (index, path) => {
    setActiveItem(index);
    if (path === '/login') {
      navigate('/login');
    }
  };

  const handleMouseEnter = (e) => {
    const target = e.currentTarget;
    const rect = target.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    const cursor = target.querySelector('.cursor-effect');
    if (cursor) {
      cursor.style.left = `${x}px`;
      cursor.style.top = `${y}px`;
      cursor.classList.add('active');
    }
  };

  const handleMouseLeave = (e) => {
    const cursor = e.currentTarget.querySelector('.cursor-effect');
    if (cursor) {
      cursor.classList.remove('active');
    }
  };

  // Scroll effect for navbar
  useEffect(() => {
    const handleScroll = () => {
      const isScrolled = window.scrollY > 10;
      if (isScrolled !== scrolled) {
        setScrolled(isScrolled);
      }
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll();

    return () => window.removeEventListener('scroll', handleScroll);
  }, [scrolled]);

  // Close dropdown when route changes
  useEffect(() => {
    setHoveredItem(null);
  }, [location]);

  return (
    <>
      <motion.nav 
        ref={navbarRef}
        className={`navbar ${scrolled ? 'scrolled' : ''}`}
        initial={{ y: -100, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ type: 'spring', stiffness: 100, damping: 20 }}
      >
        <div className="nav-container">
          <Link 
            to="/" 
            className="nav-logo hover-effect"
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
          >
            <span className="cursor-effect"></span>
            mygate
          </Link>

          <motion.ul 
            className="nav-links"
            variants={containerVariants}
            initial="hidden"
            animate="visible"
          >
            {navItems.map((item, index) => (
            <motion.li 
              key={index}
              className={`nav-item ${activeItem === index ? 'active' : ''} ${item.dropdown ? 'has-dropdown' : ''}`}
              variants={itemVariants}
              onMouseEnter={(e) => {
                setActiveItem(index);
                if (item.dropdown) setHoveredItem(index);
                handleMouseEnter(e);
              }}
              onMouseLeave={() => {
                setActiveItem(null);
                setHoveredItem(null);
                handleMouseLeave();
              }}
              whileHover={{ y: -2 }}
              whileTap={{ scale: 0.98 }}
            >
              {item.path === '/login' ? (
                <Link 
                  to={item.path} 
                  className="nav-link hover-effect"
                  onMouseEnter={handleMouseEnter}
                  onMouseLeave={handleMouseLeave}
                >
                  <span className="cursor-effect"></span>
                  {item.name}
                </Link>
              ) : (
                <span 
                  className="nav-link hover-effect"
                  onClick={() => handleNavClick(index, item.path)}
                  onMouseEnter={handleMouseEnter}
                  onMouseLeave={handleMouseLeave}
                >
                  <span className="cursor-effect"></span>
                  {item.name}
                  {item.dropdown && <span className="dropdown-arrow">▼</span>}
                </span>
              )}

              <AnimatePresence>
                {item.dropdown && hoveredItem === index && (
                  <motion.div 
                    className="dropdown-menu"
                    variants={dropdownVariants}
                    initial="hidden"
                    animate="visible"
                    exit="exit"
                  >
                    {item.dropdown.map((subItem, subIndex) => (
                      <motion.div
                        key={subIndex}
                        initial={{ opacity: 0, y: 10 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ delay: 0.1 * subIndex }}
                      >
                        <Link 
                          to={subItem.path}
                          className="dropdown-item hover-effect"
                          onClick={() => setHoveredItem(null)}
                          onMouseEnter={handleMouseEnter}
                          onMouseLeave={handleMouseLeave}
                        >
                          <span className="cursor-effect"></span>
                          {subItem.name}
                        </Link>
                      </motion.div>
                    ))}
                  </motion.div>
                )}
              </AnimatePresence>

              <span className="nav-underline"></span>
            </motion.li>
            ))}
          </motion.ul>

          <motion.div
            initial={{ x: 20, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            transition={{ delay: 0.3 }}
            className="nav-actions"
          >
            <motion.button 
              className="book-btn hover-effect"
              onClick={() => setShowDemoModal(true)}
              onMouseEnter={handleMouseEnter}
              onMouseLeave={handleMouseLeave}
              whileHover={{ y: -2, scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
            >
              <span className="cursor-effect"></span>
              <span>Book a Demo</span>
              <span className="btn-underline"></span>
            </motion.button>
          </motion.div>
        </div>
      </motion.nav>

      <AnimatePresence>
        {showDemoModal && (
          <BookDemoModal onClose={() => setShowDemoModal(false)} />
        )}
      </AnimatePresence>
    </>
  );
}

export default Navbar;
