import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './App.css';
import BookDemoModal from './components/BookDemoModal';

function Navbar() {
  const [activeItem, setActiveItem] = useState(null);
  const [hoveredItem, setHoveredItem] = useState(null);
  const navigate = useNavigate();

  const navItems = [
    { 
      name: 'Communities', 
      path: '/',
      dropdown: [
        { name: 'Apartments', path: '/apartments' },
        { name: 'Gated Communities', path: '/gated-communities' },
        { name: 'Villas', path: '/villas' },
        { name: 'Plotted Development', path: '/plotted' }
      ]
    },
    { 
      name: 'Locks', 
      path: '/locks',
      dropdown: [
        { name: 'Smart Locks', path: '/smart-locks' },
        { name: 'Digital Locks', path: '/digital-locks' },
        { name: 'Access Control', path: '/access-control' }
      ]
    },
    { 
      name: 'Brands', 
      path: '/brands',
      dropdown: [
        { name: 'Our Technology', path: '/technology' },
        { name: 'Partners', path: '/partners' },
        { name: 'Success Stories', path: '/success-stories' }
      ]
    },
    { 
      name: 'Business', 
      path: '/business'
      // No dropdown for Business
    },
    { 
      name: 'Company', 
      path: '/company',
      dropdown: [
        { name: 'About Us', path: '/about' },
        { name: 'Careers', path: '/careers' },
        { name: 'Blog', path: '/blog' },
        { name: 'Contact', path: '/contact' }
      ]
    },
    { 
      name: 'Login', 
      path: '/login'
      // No dropdown for Login
    }
  ];

  const handleNavClick = (index, path) => {
    setActiveItem(index);
    if (path === '/login') {
      navigate('/login');
    }
  };

  const [showDemoModal, setShowDemoModal] = useState(false);

  return (
    <>
      {/* Custom Cursor Effect */}
      <div 
        ref={cursorRef}
        className="custom-cursor"
        style={{
          left: `${cursorPos.x}px`,
          top: `${cursorPos.y}px`,
        }}
      />

      <nav className="navbar">
        <Link 
          to="/" 
          className="nav-logo hover-effect"
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
        >
          mygate
        </Link>
        <ul className="nav-links">
          {navItems.map((item, index) => (
            <li 
              key={index}
              className={`nav-item ${activeItem === index ? 'active' : ''} ${item.dropdown ? 'has-dropdown' : ''}`}
              onMouseEnter={(e) => {
                setActiveItem(index);
                if (item.dropdown) setHoveredItem(index);
                handleMouseEnter(e);
              }}
              onMouseLeave={() => {
                setActiveItem(null);
                setHoveredItem(null);
                handleMouseLeave();
              }}
            >
              {item.path === '/login' ? (
                <Link to={item.path} className="nav-link hover-effect">
                  {item.name}
                </Link>
              ) : (
                <span 
                  className="nav-link hover-effect"
                  onClick={() => handleNavClick(index, item.path)}
                >
                  {item.name}
                  {item.dropdown && <span className="dropdown-arrow">▼</span>}
                </span>
              )}

              {item.dropdown && hoveredItem === index && (
                <div className="dropdown-menu">
                  {item.dropdown.map((subItem, subIndex) => (
                    <Link 
                      key={subIndex} 
                      to={subItem.path}
                      className="dropdown-item hover-effect"
                      onClick={() => setHoveredItem(null)}
                    >
                      {subItem.name}
                    </Link>
                  ))}
                </div>
              )}

              <span className="nav-underline"></span>
            </li>
          ))}
        </ul>
        <button 
          className="book-btn hover-effect"
          onClick={() => setShowDemoModal(true)}
          onMouseEnter={handleMouseEnter}
          onMouseLeave={handleMouseLeave}
        >
          <span>Book a Demo</span>
          <span className="btn-underline"></span>
        </button>
      </nav>

      {showDemoModal && (
        <BookDemoModal onClose={() => setShowDemoModal(false)} />
      )}
    </>
  );
}

export default Navbar;
