import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../styles/Login.css';

function Login() {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    name: '',
    otp: ''
  });
  const [forgotPassword, setForgotPassword] = useState(false);
  const [showOtp, setShowOtp] = useState(false);
  const [otpSent, setOtpSent] = useState(false);
  const [countdown, setCountdown] = useState(0);
  const navigate = useNavigate();

  // Countdown timer for OTP resend
  useEffect(() => {
    let timer;
    if (countdown > 0) {
      timer = setTimeout(() => setCountdown(countdown - 1), 1000);
    }
    return () => clearTimeout(timer);
  }, [countdown]);

  const startOtpCountdown = () => {
    setCountdown(30); // 30 seconds countdown
  };

  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value});
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (forgotPassword) {
      // Handle forgot password logic
      alert('Password reset link sent to your email!');
      setForgotPassword(false);
      return;
    }

    if (isLogin) {
      // Handle login logic
      console.log('Login data:', { email: formData.email, password: formData.password });
      // Here you would typically call your authentication API
      // For demo, we'll just log in the user
      // navigate('/dashboard');
      return;
    }

    // Signup flow
    if (!showOtp) {
      // First step: Send OTP to email
      console.log('Sending OTP to:', formData.email);
      // Here you would typically call your API to send OTP
      setShowOtp(true);
      setOtpSent(true);
      startOtpCountdown();
    } else {
      // Second step: Verify OTP
      if (formData.otp.length === 6) { // Assuming 6-digit OTP
        console.log('Verifying OTP:', formData.otp);
        // Here you would typically verify the OTP with your backend
        // For demo, we'll just log the data and proceed
        console.log('Signup data:', {
          name: formData.name,
          email: formData.email,
          password: formData.password
        });
        alert('Account created successfully!');
        // navigate('/dashboard');
      } else {
        alert('Please enter a valid 6-digit OTP');
      }
    }
  };

  const handleResendOtp = (e) => {
    e.preventDefault();
    if (countdown === 0) {
      console.log('Resending OTP to:', formData.email);
      // Here you would typically call your API to resend OTP
      startOtpCountdown();
      alert('OTP has been resent to your email');
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <div className="login-header">
          <h2>
            {forgotPassword ? 'Reset Password' : 
             showOtp ? 'Verify Email' : 
             isLogin ? 'Login' : 'Create Account'}
          </h2>
          <p>Welcome to MyGate</p>
        </div>

        <form onSubmit={handleSubmit} className="login-form">
          {!isLogin && !forgotPassword && !showOtp && (
            <div className="form-group">
              <input
                type="text"
                name="name"
                placeholder="Full Name"
                value={formData.name}
                onChange={handleChange}
                required
                disabled={showOtp}
              />
            </div>
          )}

          <div className="form-group">
            <input
              type="email"
              name="email"
              placeholder="Email Address"
              value={formData.email}
              onChange={handleChange}
              required
              disabled={showOtp && !forgotPassword}
            />
          </div>

          {!forgotPassword && !showOtp && (
            <div className="form-group">
              <input
                type="password"
                name="password"
                placeholder="Password"
                value={formData.password}
                onChange={handleChange}
                required
              />
            </div>
          )}

          {showOtp && (
            <div className="form-group">
              <div className="otp-container">
                <input
                  type="text"
                  name="otp"
                  placeholder="Enter 6-digit OTP"
                  value={formData.otp}
                  onChange={handleChange}
                  maxLength="6"
                  pattern="\d{6}"
                  title="Please enter a 6-digit OTP"
                  required
                  className="otp-input"
                />
                <div className="otp-actions">
                  {countdown > 0 ? (
                    <span className="otp-timer">Resend in {countdown}s</span>
                  ) : (
                    <button 
                      type="button" 
                      className="text-button"
                      onClick={handleResendOtp}
                    >
                      Resend OTP
                    </button>
                  )}
                </div>
              </div>
              <p className="otp-note">We've sent a 6-digit OTP to {formData.email}</p>
            </div>
          )}

          {isLogin && !forgotPassword && !showOtp && (
            <div className="forgot-password">
              <button 
                type="button" 
                className="text-button"
                onClick={() => setForgotPassword(true)}
              >
                Forgot Password?
              </button>
            </div>
          )}

          <button type="submit" className="submit-btn">
            {showOtp ? 'Verify OTP' : 
             forgotPassword ? 'Send Reset Link' : 
             isLogin ? 'Login' : 'Continue with Email'}
          </button>

          <div className="form-footer">
            {showOtp ? (
              <p>
                <button 
                  type="button" 
                  className="text-button"
                  onClick={() => {
                    setShowOtp(false);
                    setOtpSent(false);
                    setFormData({...formData, otp: ''});
                  }}
                >
                  ← Back to Sign Up
                </button>
              </p>
            ) : forgotPassword ? (
              <p>
                Remember your password?{' '}
                <button 
                  type="button" 
                  className="text-button"
                  onClick={() => setForgotPassword(false)}
                >
                  Back to Login
                </button>
              </p>
            ) : (
              <p>
                {isLogin ? "Don't have an account? " : 'Already have an account? '}
                <button 
                  type="button" 
                  className="text-button"
                  onClick={() => {
                    setIsLogin(!isLogin);
                    setForgotPassword(false);
                    setShowOtp(false);
                    setOtpSent(false);
                  }}
                >
                  {isLogin ? 'Sign Up' : 'Login'}
                </button>
              </p>
            )}
          </div>
        </form>

        <div className="back-home">
          <Link to="/" className="back-link">
            ← Back to Home
          </Link>
        </div>
      </div>
    </div>
  );
}

export default Login;
